function Menu({ active, setActive, setCategory }) {
  const links = [
    { id: 1, name: "General", value: "general" },
    { id: 2, name: "Buisness", value: "buisness" },
    { id: 3, name: "Entertainement", value: "entertainement" },
    { id: 4, name: "Health", value: "health" },
    { id: 5, name: "Science", value: "science" },
    { id: 6, name: "Sport", value: "sport" },
    { id: 7, name: "Technology", value: "technology" },
  ];

  function onclick(id, value) {
    setActive(id);
    setCategory(value);
  }

  return (
    <nav className="menu">
      <ul>
        {links.map((link) => (
          <li
            key={link.id}
            className={active === link.id ? "active" : "inactive"}
            onclick={() => onclick(link.id, link.value)}
          >
            {link.name}
          </li>
        ))}
      </ul>
    </nav>
  );
}

export default Menu;
